reduce.reduceTypeScriptComponentExample:
const PlanetExpressCrewWeeklySalaries: React.FC = () => {
    const salaries: number[] = [5000, 4500, 2500, -200, 1000, -200, 1500];

    const totalCrewWeeklySalary: number = salaries.reduce((accumulator, currentSalary) => {
        return accumulator + currentSalary;
    }, 0);

    return (
        <div>
            <h1>Total Weekly Salary Expense</h1>
            <h3>{totalCrewWeeklySalary}</h3>
        </div>
    );
};

export default PlanetExpressCrewWeeklySalaries;
